// Helper function to create duplicate checklists
function createDuplicateChecklist(baseChecklist: EventChecklist, index: number, month: string, day: number, topic: string): EventChecklist {
  return {
    ...baseChecklist,
    id: `event-checklist-${month.toLowerCase()}-${day}-${index}`,
    eventTopic: topic,
    eventDate: `${month} ${day}, 2025`,
    tasks: baseChecklist.tasks.map((task, taskIdx) => ({
      ...task,
      id: `${month.toLowerCase()}-${day}-${index}-task-${String(taskIdx + 1).padStart(3, '0')}`
    })),
    createdAt: new Date().toISOString(),
    updatedAt: new Date().toISOString()
  };
}

